import React, { useState } from 'react';

const ImageUploadComponent = () => {
    const [images, setImages] = useState(Array(10).fill(null));

    const handleImageUpload = (e: any, index: any) => {
        const file = e.target.files[0];
        const newImages = [...images];
        newImages[index] = URL.createObjectURL(file);
        setImages(newImages);
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Upload Banners</h1>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
                {images.map((image, index) => (
                    <div key={index} className="border p-4 rounded-lg shadow-lg">
                        <label htmlFor={`upload-${index}`} className="block mb-2 text-center text-sm font-medium text-gray-700 cursor-pointer">
                            {`Upload Banner ${index + 1}`}
                        </label>
                        <input
                            id={`upload-${index}`}
                            type="file"
                            accept="image/*"
                            className="hidden"
                            onChange={(e) => handleImageUpload(e, index)}
                        />
                        {image && <img src={image} alt={`Banner ${index + 1}`} className="w-full h-32 object-cover mt-2 rounded" />}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ImageUploadComponent;
