import React, { useState } from "react";
import Footer from "../footer/Footer";
import Navbar from "../navbar/Navbar";
import { useNavigate } from "react-router-dom";
import SuccessModal from "./SuccessModal";
import GlobalModal from "../GlobalModal";
import { registerDistributor } from "../../services/authServices";
import ImageUploadComponent from "../ImageUpload";

const RegistrationForm: React.FC = () => {
  const [termsOpen, setTermsOpen] = useState(false);
  const [uploadImages, setUploadImages] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    companyName: "",
    email: "",
    mobileNumber: "",
    password: "",
    address: "",
    terms: false,
    fileUpload: null,
  });
  const [errors, setErrors] = useState({
    fullName: "",
    companyName: "",
    email: "",
    mobileNumber: "",
    password: "",
    address: "",
    terms: "",
    fileUpload: "",
  });
  const [successModalOpen, setSuccessModalOpen] = useState(false);
  const [distributorId, setDistributorId] = useState("");
  const navigate: any = useNavigate();

  const handleTermsOpen = (
    e: React.MouseEvent<HTMLSpanElement, MouseEvent>
  ) => {
    e.preventDefault();
    setTermsOpen(true);
  };

  const handleTermsClose = () => {
    setTermsOpen(false);
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value, type, checked, files } = e.target as any;
    setFormData({
      ...formData,
      [name]:
        type === "checkbox" ? checked : type === "file" ? files[0] : value,
    });
    setErrors({
      ...errors,
      [name]: "",
    });
  };

  const validateForm = () => {
    let formErrors = { ...errors };
    let isValid = true;

    if (!formData.fullName) {
      formErrors.fullName = "Full Name is required.";
      isValid = false;
    }
    if (!formData.email) {
      formErrors.email = "Email is required.";
      isValid = false;
    }
    if (!formData.mobileNumber || !/^\d{10}$/.test(formData.mobileNumber)) {
      formErrors.mobileNumber = "Mobile Number must be exactly 10 digits.";
      isValid = false;
    }
    if (!formData.password) {
      formErrors.password = "Password is required.";
      isValid = false;
    }
    if (!formData.address) {
      formErrors.address = "Address is required.";
      isValid = false;
    }
    if (!formData.terms) {
      formErrors.terms = "You must agree to the terms and conditions.";
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (validateForm()) {
      // Create a FormData object to handle file uploads
      const formData_: any = new FormData();
      formData_.append("email", formData.email);
      formData_.append("password", formData.password);
      formData_.append("fullName", formData.fullName);
      formData_.append("mobileNumber", formData.mobileNumber);
      formData_.append("address", formData.address);

      // Append file if it exists
      if (formData.fileUpload) {
        formData_.append("fileUpload", formData.fileUpload);
      }

      try {
        setUploadImages(true);
        // Send form data to the server
        await registerDistributor(formData_);
        setSuccessModalOpen(true);
        // Simulate form submission and generate a Distributor ID
        const generatedDistributorId = "DIS123456"; // Example ID, replace with actual logic
        setDistributorId(generatedDistributorId);
        setSuccessModalOpen(true);
      } catch (error) {
        console.error("Error registering distributor:", error);
      }
    }
  };

  const handleSuccessModalClose = () => {
    setSuccessModalOpen(false);
    navigate("/login"); // Navigate to the login page
  };

  const handleTermsModalClose = () => {
    setTermsOpen(false);
  };

  const handleNextBtn = () => {
    validateForm();
    setUploadImages(true);
  };

  return (
    <>
      <Navbar />
      <div>
        {uploadImages ? <>
          <ImageUploadComponent />
          <button
            onClick={handleSubmit}
